(()=>{"use strict";class t{static async getCategorias(){const t=await fetch("https://api.chucknorris.io/jokes/categories");return await t.json()}static async getFrase(t){const e=await fetch(`https://api.chucknorris.io/jokes/random?category=${t}`);return(await e.json()).value}}document.addEventListener("DOMContentLoaded",(()=>{!async function(){const e=await t.getCategorias(),a=document.getElementById("categorias");e.forEach((t=>{const e=document.createElement("tr"),n=document.createElement("td"),o=document.createElement("a");o.href=`frases.html?categoria=${t}`,o.textContent=t,n.appendChild(o),e.appendChild(n),a.appendChild(e)}))}(),document.getElementById("fraseAleatoria").addEventListener("click",(async()=>{const e=await t.getCategorias(),a=e[Math.floor(Math.random()*e.length)];window.location.href=`frases.html?categoria=${a}`})),document.getElementById("verFavoritas").addEventListener("click",(()=>{window.location.href="favoritos.html"}))}))})();